document.addEventListener("alpine:init",()=>{Alpine.data("navigation",()=>({open:!1,toggle(){this.open=!this.open}})),Alpine.data("testimonials",()=>({current:0,testimonials:[{id:1,name:"John Smith",role:"Restaurant Owner",image:"/images/testimonials/person1.jpg",rating:5,text:"This food tracing system has revolutionized how we source ingredients. Our customers appreciate the transparency."},{id:2,name:"Sarah Johnson",role:"Food Safety Inspector",image:"/images/testimonials/person2.jpg",rating:5,text:"As a food safety professional, I can attest to the effectiveness of this system. It makes my job easier and food safer."},{id:3,name:"Michael Chen",role:"Organic Farmer",image:"/images/testimonials/person3.jpg",rating:4,text:"Being able to show customers exactly how our produce is grown and handled has increased trust and sales."}],next(){this.current=(this.current+1)%this.testimonials.length},prev(){this.current=(this.current-1+this.testimonials.length)%this.testimonials.length}})),Alpine.data("teamMembers",()=>({current:0,visibleItems:3,team:[{id:1,name:"Afiq Fahmi",role:"Developer Team",image:"/images/team/afiq.png",shortBio:"Leads our vision for transparent food tracing systems worldwide.",bio:"With over 15 years of experience in food safety and supply chain management, Paul founded our company to revolutionize how food is traced from farm to table.",linkedin:"https://www.linkedin.com/in/afiqfahmi-jamil/",twitter:"https://twitter.com/",email:"paul@foodtracingsystem.com"},{id:2,name:"Naqibah Azman",role:"Community Builder",image:"/images/team/naqiba.jpg",shortBio:"Oversees the development of our blockchain-based tracing technology.",bio:"Lily brings her expertise in distributed systems and blockchain to ensure our platform is secure, scalable, and reliable for all stakeholders.",linkedin:"https://www.linkedin.com/in/naqibah-azman-848970109/",twitter:"https://twitter.com/",email:"lily@foodtracingsystem.com"},{id:3,name:"Mohd Noor",role:"Developer Team",image:"/images/team/amelia.jpg",shortBio:"Creates intuitive interfaces that make food tracing accessible to everyone.",bio:"Amelia focuses on designing user experiences that simplify complex tracing data, making our system accessible to farmers, distributors, retailers, and consumers.",linkedin:"https://linkedin.com/",twitter:"https://twitter.com/",email:"amelia@foodtracingsystem.com"},{id:4,name:"Marco O'Reilly",role:"Technical Developer",image:"/images/team/marco.jpg",shortBio:"Builds the backend systems that power our food tracing platform.",bio:"Marco specializes in developing robust APIs and data processing systems that handle millions of food tracing events daily with precision and reliability.",linkedin:"https://linkedin.com/",twitter:"https://twitter.com/",email:"marco@foodtracingsystem.com"},{id:5,name:"Lakshmi Patel",role:"UX Research Lead",image:"/images/team/lakshmi.jpg",shortBio:"Ensures our solutions meet the real needs of users across the food supply chain.",bio:"Through extensive field research and user testing, Lakshmi helps us understand the challenges faced by different stakeholders and design solutions that truly work for them.",linkedin:"https://linkedin.com/",twitter:"https://twitter.com/",email:"lakshmi@foodtracingsystem.com"}],next(){this.current<this.team.length-this.visibleItems&&this.current++},prev(){this.current>0&&this.current--}}))}),document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("map-section");if(e){const t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(initMap(),t.unobserve(e.target))})},{threshold:.1});t.observe(e)}});function initMap(){if(typeof L!="undefined"){const e=L.map("tracing-map").setView([40.7128,-74.006],4);L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png",{attribution:'&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'}).addTo(e);const t=[{lat:42.3601,lng:-71.0589,name:"Organic Farm A"},{lat:37.7749,lng:-122.4194,name:"Sustainable Farm B"},{lat:29.7604,lng:-95.3698,name:"Local Producer C"}],n=[{lat:40.7128,lng:-74.006,name:"Processing Facility X"},{lat:41.8781,lng:-87.6298,name:"Packaging Center Y"}],s=[{lat:39.9526,lng:-75.1652,name:"Distribution Hub East"},{lat:34.0522,lng:-118.2437,name:"Distribution Hub West"}];t.forEach(t=>{L.marker([t.lat,t.lng]).addTo(e).bindPopup(`<b>${t.name}</b><br>Farm Production Stage`)}),n.forEach(t=>{L.marker([t.lat,t.lng]).addTo(e).bindPopup(`<b>${t.name}</b><br>Processing & Packaging Stage`)}),s.forEach(t=>{L.marker([t.lat,t.lng]).addTo(e).bindPopup(`<b>${t.name}</b><br>Distribution Stage`)});const o=[[42.3601,-71.0589],[40.7128,-74.006],[39.9526,-75.1652]];L.polyline(o,{color:"#22c55e",weight:3,opacity:.7}).addTo(e)}}